import{arcLengthDeg,arcLengthRad}from"./arc-length.js";describe("The arc length",(function(){it("should be zero when the radius and angle are not provided",(function(){expect(arcLengthDeg()).toBe(0);expect(arcLengthRad()).toBe(0)}));it("should be correctly calculated for positive inputs",(function(){expect(arcLengthDeg(20,0)).toBe(0);expect(arcLengthDeg(20,90)).toBe(31.41592653589793);expect(arcLengthDeg(20,360)).toBe(125.66370614359172);expect(arcLengthDeg(20,450.617)).toBe(157.29500630918577);expect(arcLengthDeg(0,90)).toBe(0);expect(arcLengthDeg(45.34,90)).toBe(71.21990545688061);expect(arcLengthDeg(100,90)).toBe(157.07963267948966);expect(arcLengthDeg(890.67,90)).toBe(1399.0611643864104);expect(arcLengthRad(20,0)).toBe(0);expect(arcLengthRad(20,90)).toBe(1800);expect(arcLengthRad(20,360)).toBe(7200);expect(arcLengthRad(20,450.617)).toBe(9012.34);expect(arcLengthRad(0,90)).toBe(0);expect(arcLengthRad(45.34,90)).toBe(4080.6000000000004);expect(arcLengthRad(100,90)).toBe(9e3);expect(arcLengthRad(890.67,90)).toBe(80160.3)}));it("should be negative for negative inputs",(function(){expect(arcLengthDeg(20,-90)).toBe(-31.41592653589793);expect(arcLengthDeg(20,-360)).toBe(-125.66370614359172);expect(arcLengthDeg(20,-450.617)).toBe(-157.29500630918577);expect(arcLengthDeg(-20,90)).toBe(-31.41592653589793);expect(arcLengthDeg(-45.34,90)).toBe(-71.21990545688061);expect(arcLengthDeg(-100,90)).toBe(-157.07963267948966);expect(arcLengthDeg(-890.67,90)).toBe(-1399.0611643864104);expect(arcLengthRad(20,-90)).toBe(-1800);expect(arcLengthRad(20,-360)).toBe(-7200);expect(arcLengthRad(20,-450.617)).toBe(-9012.34);expect(arcLengthRad(-20,90)).toBe(-1800);expect(arcLengthRad(-45.34,90)).toBe(-4080.6000000000004);expect(arcLengthRad(-100,90)).toBe(-9e3);expect(arcLengthRad(-890.67,90)).toBe(-80160.3)}));it("should not be properly defined for incorrect inputs",(function(){expect(arcLengthDeg(20,NaN)).toBeNaN();expect(arcLengthDeg(NaN,NaN)).toBeNaN();expect(arcLengthDeg(NaN,20)).toBeNaN();expect(arcLengthDeg(20,null)).toBe(0);expect(arcLengthDeg(null,null)).toBe(0);expect(arcLengthDeg(null,20)).toBe(0);expect(arcLengthRad(20,NaN)).toBeNaN();expect(arcLengthRad(NaN,NaN)).toBeNaN();expect(arcLengthRad(NaN,20)).toBeNaN();expect(arcLengthRad(20,null)).toBe(0);expect(arcLengthRad(null,null)).toBe(0);expect(arcLengthRad(null,20)).toBe(0)}))}));describe("The arc length, when calculated from degrees,",(function(){it("should not clamp the angle between 0 and 360 degrees",(function(){expect(arcLengthDeg(20,-90)).toBe(-31.41592653589793);expect(arcLengthDeg(20,-180)).toBe(-62.83185307179586);expect(arcLengthDeg(20,450.617)).toBe(157.29500630918577);expect(arcLengthDeg(20,540)).toBe(188.49555921538757)}))}));